import { useState } from "react"

import styled from "styled-components"
import Card from "./components/Card"

import Title from "./components/Title"
import SubTitle from "./components/SubTitle"
import Headline from "./components/Headline"
import List from "./components/List"

import rfidLogo from "../src/images/rfid-logo.png"
import nfcLogo from "../src/images/nfc-logo.png"
import schaubild1 from "../src/images/schaubild-1.png"
import schaubild2 from "../src/images/schaubild-2.png"
import schaubild3 from "../src/images/schaubild-3.png"
import schaubild4 from "../src/images/schaubild-4.png"

// - Aufzählung, Stichpunkte
// - Bild
// - Bild Slideshow
// - Nfc server support?
// - Bar code zur website in der endcard

import EndCard from "./templates/EndCard"

import Keys from "./components/Keys"
import Gallery from "./components/Gallery"

const Table = styled.table`
	outline: 1px solid white;
	grid-row: 3 / 12;
	grid-column: 2 / 12;
	border-spacing: 1rem;
	width: 100%;
	table-layout: fixed;
	margin-left: -1rem;
	tr {
		white-space: nowrap;

		td:first-of-type {
			/* text-align: right; */
		}
		th,
		td {
			/* outline: 1px solid white; */
			font-size: 1.5rem;
			will-change: auto;
			vertical-align: middle;
			font-weight: 100;
		}
		th {
			font-weight: 400;
			color: coral;
		}
	}
`

const Data = () => {
	const [current, setcurrent] = useState(0)
	const [functionCurrent, setFunctionCurrent] = useState(0)

	const images = ["", rfidLogo, nfcLogo, ""]
	const schaubilder = [schaubild1, schaubild2, schaubild3, schaubild4]

	const subText = [
		"NFC / RFID - Lesegerät liest Daten aus einer Smartcard aus.",
		"Reader-Emulation: Ein Smartphone liest Daten aus einem NFC Tag aus. Smartphone -> NFC/RFID Lesegerät",
		"Card-Emulation: Ein NFC - Lesegerät liest Daten aus einem Smartphone. Smartphone -> Smartcard",
		"Beide NFC Geräte befinden sich im aktiven Modus und kommunizieren miteinander.",
	]

	const subList = [
		// allgemein
		[
			"sind gobale und draht- bzw. kabellose Kommunikation-Technologien",
			"Austausch von Daten und Informationen",
		],
		// rfid
		[
			"Radio-Frequency Identification",
			"Niederfrequenz (LF)",
			"Hochfrequenz (HF)",
			"Ultrahochfrequenz (UHF)",
		],
		[
			"Near-Field-Communication",
			"Das Lesegerät agiert nicht nur als Lesegerät, sondern auch als Tag (Card Emulation)",
			"Außerdem ist eine Peer to Peer Kommunikation möglich.",
		],
		[
			"Lesegerät und Transponder",
			"Transpoder (Tags) gibt es in aktiver und passiver Form Die aktive Tags beinhalten eine eigene Stromquelle, passive Tags hingegen werden über den Reader mit Energie versorgt.",
			"LF & HF -> wenige Zentimeter; UHF -> bis zu über einem Meter",
		],
	]

	const SubText = styled.p`
		/* outline: 1px solid red; */
		align-self: center;
		font-weight: 100;
		line-height: 1.3;
		grid-column: 7 / 12;
		grid-row: 3 / 11;
		font-size: 1.5rem;
	`

	const SubList = styled.ul`
		grid-column: 7 / 12;
		grid-row: 3 / 11;
		align-self: center;
		list-style: inherit;
		li {
			font-size: 1.5rem;
			margin-left: 1.2rem;
			margin-bottom: 1rem;
			line-height: 1.2;
			font-weight: 300;
		}
	`

	return (
		<>
			<Card>
				<Title value="RFID & NFC" />
				<SubTitle value="Endlich wieder etwas Nähe" />
				<Keys />
			</Card>
			<Card>
				<Headline value="Was sind RFID und NFC?" />
				<Gallery
					current={current}
					images={images}
					setcurrent={setcurrent}
					max={images.length}
				/>
				<SubList>
					{subList[current].map((value) => (
						<li>{value}</li>
					))}
				</SubList>
				{/* <SubText>{subText[current]}</SubText> */}
				{/* 
            // Gallery
            // List

        */}
			</Card>

			<Card>
				<Headline value="Funktionsweise"></Headline>
				<Gallery
					current={functionCurrent}
					images={schaubilder}
					setcurrent={setFunctionCurrent}
					max={schaubilder.length}
				/>
				<SubText>{subText[functionCurrent]}</SubText>
			</Card>
			<Card>
				<Table>
					<thead>
						<tr>
							<th></th>
							<th>Low Frequency</th>
							<th>High Frequency</th>
							<th>Ultra High Frequency</th>
						</tr>
					</thead>
					<tbody>
						<tr>
							<td>Frequenz-bereich</td>
							<td>125kHz – 135 kHz</td>
							<td>13,56 MHz</td>
							<td></td>
						</tr>
						<tr>
							<td></td>
							<td></td>
							<td></td>
							<td></td>
						</tr>
						<tr>
							<td></td>
							<td></td>
							<td></td>
							<td></td>
						</tr>
						<tr>
							<td></td>
							<td></td>
							<td></td>
							<td></td>
						</tr>
						<tr>
							<td></td>
							<td></td>
							<td></td>
							<td></td>
						</tr>
						<tr>
							<td></td>
							<td></td>
							<td></td>
							<td></td>
						</tr>
						<tr>
							<td></td>
							<td></td>
							<td></td>
							<td></td>
						</tr>
					</tbody>
				</Table>
			</Card>

			<Card>
				<Headline value="Wie und wozu lässt sich die Technologie einsetzen?" />
				<List
					data={[
						"Globale und draht- bzw. kabellose Kommunikations-Technologien",
						"Radio-Frequency Identification ermöglicht Kommunikation zwischen einem Tag (Transponder, ist eine Sende- / Empfangseinheit) ohne Stromversorgung und einem elektrisch betriebenen Lesegerät (Reader mit Antenne)",
						"Daten lassen sich berührungslos und ohne Sichtkontakt speichern, Transponder wird von außen drahtlos induktiv oder per Funksignal angesprochen → gibt Daten preis",
						"Near-Field-Communication basiert auf RFID-Protokollen, NFC Gerät kann auch als Tag agieren (Card Emulation Mode)→im Peer to Peer Mode ist Übertragung von Informationen zwischen zwei NFC Geräten möglich",
						"NFC mit sehr kurzer Reichweite (paar Zentimeter)",
					]}
				/>
